import React from "react";
import {Row, Col, Statistic, Spin, List, Card} from "antd";

const cardStyle = {
	background: "#ffffff",
	padding: 25,
	textAlign: "center",
	border: "1px solid #D3D3D3"
};

const CountryCoVidDataComponent = (props) => {
	const {countryStat, isLoading} = props;
	return (
		<Row style={cardStyle}>
			{countryStat.map(data => {
				const {total_cases, active_cases, total_deaths, total_recovered} = data;
				return (
					<>
						{isLoading ? (
							<Spin style={{margin: "auto"}} size='large'/>
						) : (
							<>

								<Card>
									<Row>
										<Col span={16}>
											<p>Total Cases</p>
											<p>Active Cases</p>
											<p>Total Deaths</p>
											<p>Total Recovered</p>
										</Col>
										<Col span={8}>
											<p>{total_cases}</p>
											<p>{total_recovered}</p>
											<p>{total_deaths}</p>
											<p>{active_cases}</p>
										</Col>
									</Row>
								</Card>
							</>)
						}
					</>
				);
			})}
		</Row>
	);
};

export default CountryCoVidDataComponent;
