import React from "react";
import {Row, Col, Statistic, List, Card} from "antd";

const cardStyle = {
	background: "#ffffff",
	padding: 30,
	textAlign: "center",
	border: "1px solid #D3D3D3"
};

const CountryCoVidDataLoading = () => {
	return (
		<>
			<List
				size="small"
				header={<div>Header</div>}
				footer={<div>Footer</div>}
				bordered
				dataSource='data'
				renderItem={item => <List.Item>{item}</List.Item>}
			/>
			<Card>

			</Card>
		<Row style={cardStyle}>
			<Col span={6}>
				<Statistic title='Total Cases' value={0}/>
			</Col>
			<Col span={6}>
				<Statistic title="Active Cases" value={0}/>
			</Col>
			<Col span={6}>
				<Statistic title="Total Deaths" value={0}/>
			</Col>
			<Col span={6}>
				<Statistic title="Total Recovered" value={0}/>
			</Col>
		</Row>
			</>
	);
};

export default CountryCoVidDataLoading;
